import React, {useState} from 'react';
import Header from './Header';

function App() {
  const [counter, setCounter] = useState(0); //useState retorna array [valor, func atualização]
  function increment(){
    setCounter(counter + 1);
  }
  return (
    <div>
      <Header>Contador: {counter}</Header>
      <button onClick={increment}>Inscrementar</button>
    </div>
  );
}

export default App;
